        public CustomerRepository1()
        {
            Clock = new CSystemClock();
            Log = new CLog();
        }
        public CCustomer Get(Guid id)
        {
            CCustomer result;
            if(!_customers.TryGetValue(id, out result!))
            {
                Log.Error(Clock.Now, "no such customer");
                throw new ArgumentException("no such customer");
            }
            return result;
        }

