using CustomerRepository.Model;
using CustomerRepository.ServiceLocator;
using System;
using System.Collections.Generic;

namespace CustomerRepositoryTests.Model
{
    /// <summary>Class CustomerRepository4.
    /// Implements the <see cref="ICustomerRepository" /></summary>
    /// <remarks>This repository uses a <b>Service Locator</b></remarks>
    /// <autogeneratedoc />
    public class CustomerRepository4 : ICustomerRepository
    {
        #region Properties
        #region private properties
        private readonly Dictionary<Guid,CCustomer> _customers = new();
        private IClock _Clock { get; }
        private ILog _Log { get; }
        #endregion

        public long Count => _customers.Count;
        #endregion

        public CustomerRepository4()
        {
            _Clock = ServiceLocator.Clock;
            _Log = ServiceLocator.Log;
        }

        public CCustomer? Get(Guid id)
        {
            if (!_customers.TryGetValue(id, out CCustomer? result))
            {
                _Log.Error(_Clock.Now, "no such customer");
                throw new ArgumentException("no such customer");
            }
            return result;
        }

        public Guid Put(CCustomer customer)
        {
            Guid g = Guid.NewGuid();
            _customers[g] = customer;
            return g;
        }


    }
}