using System;
using System.Threading;
class P { static void Main() { Random r = new Random(); int g = r.Next(1, 21); string[] t = { "Stdabw...", "Fourier...", "Norm...", "Kovarianz...", "MonteCarlo..." }; bool e = false; while (!e) { Console.Write("In: "); string s = Console.ReadLine(); Console.WriteLine(t[r.Next(t.Length)]); for (int i = 0; i <= 20; i++) { Console.Write("[" + new string('#', i) + new string(' ', 20 - i) + $"] {i * 5}%\r"); Thread.Sleep(30); } Console.WriteLine(); if (int.TryParse(s, out int z)) { if (z == g) { Console.WriteLine("Treffer!"); e = true; } else if (z < g) Console.WriteLine("Zu klein"); else Console.WriteLine("Zu groß"); Console.WriteLine("Avg:" + r.Next(10, 100)); Console.WriteLine("Var:" + r.NextDouble().ToString("F3")); Console.WriteLine("Corr:" + r.NextDouble().ToString("F2")); } else Console.WriteLine("Ungültig"); } } }
