/// <summary>
/// The Logging namespace.
/// </summary>
/// <autogeneratedoc />
namespace JCAMS.Core.Logging
{
    /// <summary>
    /// Interface IProtocol
    /// </summary>
    /// <autogeneratedoc />
    public interface IProtocol
    {
        /// <summary>
        /// Writes the specified Text and gives an intervall [msec].
        /// </summary>
        /// <param name="Intervall_mSec">The intervall [msec].</param>
        /// <param name="Text">The text.</param>
        /// <returns><c>true</c> if XXXX, <c>false</c> otherwise.</returns>
        /// <autogeneratedoc />
        bool Write(int Intervall_mSec, string Text);

        /// <summary>
        /// Writes the specified Text.
        /// </summary>
        /// <param name="Text">The text.</param>
        /// <returns><c>true</c> if XXXX, <c>false</c> otherwise.</returns>
        /// <autogeneratedoc />
#if NET6_0_OR_GREATER
        bool Write(string Text)
        => Write(0, Text);
#else
        bool Write(string Text);
#endif

        /// <summary>
        /// Gets a value indicating whether this instance is event log.
        /// </summary>
        /// <value><c>true</c> if this instance is event log; otherwise, <c>false</c>.</value>
        /// <autogeneratedoc />
        bool IsEventLog { get; }
        /// <summary>
        /// Gets a value indicating whether this instance is file.
        /// </summary>
        /// <value><c>true</c> if this instance is file; otherwise, <c>false</c>.</value>
        /// <autogeneratedoc />
        bool IsFile { get; }
        /// <summary>
        /// Gets the filename.
        /// </summary>
        /// <value>The filename.</value>
        /// <autogeneratedoc />
        string Filename { get; }
    }
}